/* For brevity, include a small shim or recommend using CDN. */
// Minimal stub of Fuse.js; for production replace with full library
window.Fuse = function(list, opts) {
  this.list = list || [];
  this.search = function(q) {
    if (!q) return [];
    q = q.toLowerCase();
    return this.list.filter(item => (item.title+" "+item.content).toLowerCase().includes(q)).map(i=>({item:i}));
  };
};
